// Save the global config settings to the database

var msgs = [];  // An array of database query messages

for (var item of ['year', 'model', 'floorplan', 'aquahot', 'floorheat', 'ceilfan', 'tvlift', 'thirdtstat', 'secondfurnace', 'midheat', 'rearheat', 'busundercoach', 'powerbed', 'shades_interior']) {
    var newmsg = {};
    newmsg.topic = 'INSERT OR REPLACE INTO configs (key, value) VALUES(?, ?)';
    newmsg.payload = [item, String(flow.get(item))];
    msgs.push(newmsg);
}

return [msgs, {payload: 'Saving changes...'}];

// Save the user's selection for future use
if (msg.payload !== '') {
    flow.set('floorplan', msg.payload);
    return msg;
}

// msg.payload = msg.payload[0];
// return msg;

var payload = {};
for (var i = 0; i < (msg.payload).length; i++) {
    var key = msg.payload[i].key;
    var val = msg.payload[i].value;
    flow.set(key, val);
    if (val !== '') {
        payload[key] = val;
    }
}

msg.payload = payload;
return msg;

for (var i = 0; i < (msg.payload).length; i++) {
    var key = msg.payload[i].key;
    var value = msg.payload[i].value;

    // If a JSON string is passed in, parse it and save the resulting
    // object into global context. Otherwise, just save the raw value.
    if (typeof(value) == 'string' && (value.charAt(0) == '[' || value.charAt(0) == '{')) {
        global.set('settings2.' + key, JSON.parse(value));
    } else {
        global.set('settings2.' + key, value);
    }
}

// Returning NULL would terminate the flow, but other
// nodes want to know when settings have been loaded.
return {};

// Save the provided key and value to the settings2 DB table, and
// update the settings2 global context.
var key = msg.topic;
var value = msg.payload;

// If a JSON string is passed in, parse it and save the resulting
// object into global context. Otherwise, just save the raw value.
if (typeof(value) == 'string' && (value.charAt(0) == '[' || value.charAt(0) == '{')) {
    global.set('settings2.' + key, JSON.parse(value));
} else {
    global.set('settings2.' + key, value);
}

var newmsg = {};
newmsg.topic = 'INSERT OR REPLACE INTO settings2 (key, value) VALUES(?, ?)';
newmsg.payload = [key, String(value)];

return newmsg;

