var preset = global.get('presets')[msg.payload];
var name = preset ? preset.name : '';
return { payload: name };

var presets = [];
for (let i = 1; i <= 10; i++) {
    presets[i] = { name: '' };
}

for (let i = 0; i < (msg.payload).length; i++) {
    let number = msg.payload[i].number;
    let data = JSON.parse(msg.payload[i].data);
    presets[number] = data;
}

global.set('presets', presets);

// Returning NULL would terminate the flow, but other
// nodes want to know when settings have been loaded.
return {};

// Update global context with status on whether any air
// conditioners or heat pumps are currently running.

// Sometimes zone 0 is the front, sometimes it's zone 2 (if heated floors are 0 and 1)
const firstzone = parseInt(global.get('settings2').tstat_first_zone) || 0;

const zone      = msg.zone;
const device    = msg.device;
const instance  = zone + firstzone;
const acload    = msg.payload['operating status'] > 0 ? true : false;

global.set('status.tstat[' + instance + ']["' + device + '_on"]', acload);

var template = '<div layout="column" layout-align="space-between">';
template += '<p>Voice Integration supports ' + (msg.payload).length + ' voice command devices for your coach, listed below.</p>\n';
template += '<p>Each device responds to "turn on" and "turn off" voice commands. Devices listed with a plus sign (<b>+</b>) in front of them also respond to "dim" commands.</p>\n';
template += '<p>Some example commands include: "Alexa, turn on task light", or "Alexa, dim main ceiling light to 50 percent".</p>\n';
template += '<hr>\n';

template += '<p>';
for (let i = 0; i < (msg.payload).length; i++) {
    let name = msg.payload[i].name.trim();
    let dim = msg.payload[i].dimUrl;
    let bullet = dim ? '+' : '-';
    template += bullet + ' ' + name + '<br>\n';
}
template += '</p>';

template += '</div>';
msg.template = template;

return msg;

