// Populate the "Floor Plan" menu with appropriate options based
// on the user's choice of year and model.

const years = global.get('features');

// User's choice of year and model
const year = flow.get('year');
const model = flow.get('model');

if (year && model && years[year][model]) {
  const floors = Object.keys(years[year][model]).filter(floor => floor != 'Default');

  const floormsg = { options: floors, payload: null, topic: null };

  return [floormsg];
}

const max_rules = 12;

const tests = { 'is below': '<', 'is above': '>', 'changes': 'changes' };
let notifs = global.get('settings2')['notify_rules'] || [];

notifs.sort((a,b) => a.notif_type.localeCompare(b.notif_type));

for (let notif in notifs) {
    let n = notifs[notif];
    n.notif_testval = tests[n.notif_test] + ' ' + n.notif_value + n.notif_unit;
}

return [{ payload: notifs }, { enabled: notifs.length < max_rules } ];

// Remove a notification rule, update the settings on disk,
// and refresh the list.

let notifs = global.get('settings2')['notify_rules'] || [];
notifs.splice(msg.topic, 1);  // This changes in global context

return { topic: 'notify_rules', payload: JSON.stringify(notifs) };

