for (var i = 0; i < (msg.payload).length; i++) {
    var key = msg.payload[i].key;
    var value = msg.payload[i].value;
    global.set('configs.' + key, value);
}

// Returning NULL would terminate the flow, but other
// nodes want to know when settings have been loaded.
