// Newer coaches send DC_SOURCE_STATUS_SPYDER in addition to the
// normal DC_SOURCE_STATUS. In these cases, ignore the legacy values.

let newmsg = {};

const labels = ['N/A', 'House', 'Chassis', 'House2'];
newmsg.topic = 'CP/BATTERY_VOLTS/' + labels[msg.payload.instance];
newmsg.payload = msg.payload['dc voltage'];

if ((msg.topic).includes('SPYDER')) {
    // Remember to only parse _SPYDER messages from now on
    context.set('use_spyder', true);
} else {
    // If _SPYDER messages are used, ignore legacy messages
    if (context.get('use_spyder')) {
        newmsg = null;
    }
}

msg.topic = null;  // Overwrite incoming msg topic
return newmsg;

const first_zone = global.get('settings2').tstat_first_zone || 0;
const zone_names = {
  0: 'Front',
  1: 'Mid',
  2: 'Rear',
  4: 'Wet Bay',
  5: 'Generator Bay'
};

const zone = parseInt(msg.payload.instance) - first_zone;

let newmsg = {};
if (zone in zone_names) {
  newmsg.topic = 'CP/THERMISTOR_TEMP/' + zone_names[zone];
  newmsg.payload = msg.payload['ambient temp F'];

  return newmsg;
}

// Output 1: name of current ATS power source

const genset_status = global.get('status').power.generator_state || 'stopped';

// If the rmsc value is not numeric (e.g. 'n/a'), then
// the ATS is not receiving any external power.
if (isNaN(parseFloat(msg.payload['rms current']))) {
    msg.payload = 'None';
} else if (genset_status == 'running') {
    msg.payload = 'Generator';
} else {
    msg.payload = 'Shore';
}

global.set('status.power.ac_source', msg.payload);

msg.type = 'acsource';
return msg;

