// Populate the "Aqua-Hot" toggle based on the chosen model.

var aquahot_standard = ['Allegro_Bus', 'Zephyr'];
var msg = { topic: '', payload: '' };

if (flow.get('model')) {
    if (aquahot_standard.includes(flow.get('model'))) {
        msg.payload = true;
    }
} else {
    msg = null;
}

return msg;

// Populate the "Number of Thermostats" toggle based on the chosen model.

var third_zone = ['Phaeton', 'Allegro_Bus', 'Zephyr'];
var msg = { topic: '', payload: '', enabled: true };

if (flow.get('model')) {
    if (third_zone.includes(flow.get('model'))) {
        msg.payload = true;
    }
}

if (flow.get('year') < 2018 && flow.get('model').substring(0,8) != 'VanLeigh') {
