// Populate the "Year" menu with appropriate options based
// on the features.json file.

let years = global.get('features');
years = Object.keys(years).filter(year => year[0] == 2).reverse();

const yearmsg = { options: years, payload: null, topic: null };
const blankmsg = { options: [], payload: null, topic: null }

return [yearmsg, blankmsg];

// Populate the "Floor Plan" menu with appropriate options based
// on the user's choice of year and model.

const years = global.get('features');

// User's choice of year
const year = flow.get('year');

if (year && years[year]) {
  const models = Object.keys(years[year]).filter(model => model != '_comment');

  const modelmsg = { options: models, payload: null, topic: null };
  const blankmsg = { options: [], payload: null, topic: null }

  return [modelmsg, blankmsg];
}

// Populate the "Floor Plan" menu with appropriate options based
// on the user's choice of year and model.

const years = global.get('features');

// User's choice of year and model
const year = flow.get('year');
const model = flow.get('model');

if (year && model && years[year][model]) {
  const floors = Object.keys(years[year][model]).filter(floor => floor != 'Default');

  const floormsg = { options: floors, payload: null, topic: null };

  return [floormsg];
}
